import React from "react";
import "../App.css";
import { SidebarData } from "./SidebarData";
import { useState } from "react";
import userPicture from "../assets/test/alonso.jpeg";

export default function Sidebar() {
  const [currentPage, setCurrentPage] = useState("/home");

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div className="Sidebar">
      <div className="SideBarUserInfo">
        <img
          id="user_picture"
          className="SideBarUserPicture"
          src={userPicture}
          alt="User"
        />
        <p id="user_name">Fernando Alonso</p>
      </div>

      <ul className="SidebarList">
        {SidebarData.map((val, key) => {
          return (
            <li
              className="SidebarRow"
              id={window.location.pathname === val.link ? "active" : ""}
              key={key}
              onClick={() => {
                window.location.pathname = val.link;
                handlePageChange(key.link);
              }}
            >
              <div id="icon" className="SidebarIcon">
                {val.icon}
              </div>
              {""}
              <div id="title" className="SidebarTitle">
                {val.title}
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
