import styled from "styled-components";

export const Container = styled.div`
  position: relative;
  width: 50px;
  height: 50px;
  box-sizing: border-box;
  border-radius: 50px;
  border: 4px solid #393e46;
  padding: 5px;
  background: #222831;
  transition: all 0.55;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;
export const SearchInput = styled.input`
position: absolute;
top: 0;
left: 0;
width: 100%;
height: 42px;
line-height:30px;
outline: 0;
border: 0;
font-size:2rem;
border-radius:20px;
padding: 0 20px;
margin: 0;
-moz-appearance:none;
-webkit-appearance:none;
appearance: none;

display: ${(props) => (props.showSearchInput ? 'block' : 'none')}
`;
