import React from "react";
import "../App.css";
import { SidebarData } from "./SidebarData";
import userPicture from "../assets/test/alonso.jpeg";
import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";

export default function Sidebar() {
  let location = useLocation().pathname;

  return (
    <div className="Sidebar">
      <div className="SideBarUserInfo">
        <img
          id="user_picture"
          className="SideBarUserPicture"
          src={userPicture}
          alt="User"
        />
        <p id="user_name">Fernando Alonso</p>
      </div>

      <ul className="SidebarList">
        {SidebarData.map((val, key) => {
          return (
            <Link 
              className="SidebarRow"
              id={location === val.link ? "active" : ""}
              key={key}
              to={val.link}
              onClick={() => {
                location = val.link;
              }}
            >
              <div id="icon" className="SidebarIcon">
                {val.icon}
              </div>
              {""}
              <div id="title" className="SidebarTitle">
                {val.title}
              </div>
            </Link>
          );
        })}
      </ul>
    </div>
  );
}
